EFFSHORTINDEX search_object(unsigned int o) FASTCALL
{
    EFFSHORTINDEX idx;
    for(idx=0; idx<OSIZE;++idx)
        if(obj[idx].code==o)
            return idx;
    return 0;
}

object *search_object_p(unsigned int o) FASTCALL
{
    return &obj[search_object(o)];
}
EFFSHORTINDEX search_room(EFFSHORTINDEX r) FASTCALL
{
    EFFSHORTINDEX idxl;
    for(idxl=0; idxl<RSIZE;++idxl)
        if(world[idxl].code==r)
            return idxl;
    return 0;
}

void restart(void)
{
    EFFSHORTINDEX i;
    EFFSHORTINDEX j;
    for(i=1;i<129;++i){
        marker[i]=0;
        counter[i]=0;
    }
    for(j=0; j<RSIZE;++j)
        for(i=0; i<NDIR;++i)
            world[j].directions[i]=original_connections[j][i];
    marker[124]=true;
    marker[121]=true;
    marker[125]=0;
    next_position=100;
    for(j=0; j<OSIZE;++j)
        obj[j].position=original_position[j];
}

void show_messagenlf(char *m) FASTCALL
{
    boolean o;
    cpointer=0;
    bpointer=0;
    compressed=m;
    do{
        o=decode();
        writesameln(decompress_b);
    } while(o);
}

void show_message(char *m) FASTCALL
{
    show_messagenlf(m);
    printnewline();
}

