#define inventory()\
{\
    boolean gs=false;\
    EFFSHORTINDEX i;\
    show_message(message1032);\
    for(i = 0; i<OSIZE; ++i) {\
        dummy=obj[i].position;\
        if(dummy==CARRIED||dummy==WEARED) {\
            gs=true;\
            evidence2();\
            show_messagenlf(obj[i].desc);\
            normaltxt();\
            if(dummy==WEARED){\
