void game_cycle(void)
{
    unsigned char k;
    room *cr;
    boolean ve,pa;
    while(1){
        current_position=next_position;
        ++marker[125];
        if(marker[120]==false&&(marker[121]||marker[122])) {
            printnewline();
            evidence1();
            cr=&world[search_room(current_position)];
            show_messagenlf(cr->short_d);
            printspace();
            end_evidence1();
            normaltxt();
            printspace();
            show_message(cr->long_d);
            printnewline();
            marker[120]=true;
            ve=false;
            for(k=0;k<OSIZE;++k)
                if(obj[k].position==current_position) {
                    if(ve==false) {
                        show_message(message1031);
                        ve=true;
                        evidence2();
                    }
                    show_message(obj[k].desc);
                }
            normaltxt();
            if(marker[124]) {
                pa=false;
                for(k=0; k<NDIR; ++k)
                    if(cr->directions[k]) {
                        if(pa==false) {
                            show_messagenlf(message1020);
                            pa=true;
                        }
                    evidence3();
                    show_messagenlf(dir[k]);
                    printspace();
                }
                normaltxt();
                printnewline();
            }
        }
        ++counter[125];
        --counter[126];
        --counter[127];
        --counter[128];
        hi_cond();
        if(retv) continue;
        printnewline();
        if(ls==0 && marker[125]<5) show_message(message1012);
        interrogationAndAnalysis();
        local_cond();
        if(retv) continue;
        low_cond();
        if(retv) continue;
        show_message(verb==0?message1009:message1010);
    }
}

int main(void)
{
    restart();
    init_term();
    evidence2();
    show_message(headername);
    show_message(headerauthor);
    show_message(headerdate);
    writesameln("AWS 3.0");
    normaltxt();
    waitkey();
    game_cycle();
    return 0;
}
#ifdef CV_IS_A_FUNCTION
boolean cv(unsigned char v) FASTCALL
{
    return verb==v;
}
#endif
boolean vov(unsigned int v1, unsigned int v2)
{
    return verb==v1||verb==v2;
}
boolean vovn(unsigned int v1, unsigned int v2, unsigned int n)
{
    return vov(v1,v2)&&noun1==n;
}
boolean vovn100_0(unsigned char n) FASTCALL
{
    return vovn(100,0,n);
}
boolean cvn(unsigned int v, unsigned int n)
{
    return verb==v&&noun1==n;
}
boolean cvn70(unsigned char n)
{
    return cvn(70,n);
}
boolean cva(unsigned int v, unsigned int n)
{
    return verb==v&&actor==n;
}
boolean cva75(unsigned char n)
{
    return cva(75,n);
}
boolean cva70(unsigned char n)
{
    return cva(70,n);
}
boolean cvna(unsigned int v, unsigned int n, unsigned int o)
{
    dummy=get_object_position(o);
    return verb==v&&noun1==n&&(dummy==current_position||dummy==CARRIED);
}
boolean cvna70(unsigned char n, unsigned char o)
{
   return cvna(70,n,o);
}
