boolean unwear(unsigned int o) FASTCALL
{
    dummy=search_object(o);
    odummy=&obj[dummy];
    if(odummy->position==WEARED){
        odummy->position=CARRIED;
        --counter[118];
    } else {
        show_message(message1010);
        return true;
    }
    return false;
}
